mapper.select:
with cte as ( select id, pid, cast(id as nvarchar(max)) as path, 1 as TLevel, id as mapping from dbo.station where id = ${stationId}
union all select c.id,c.pid, p.path+'-'+cast(c.id as nvarchar(max)) as path,p.TLevel+1 as TLevel, case when p.TLevel < 2 then c.id else p.mapping end as mapping from cte P inner join dbo.station c on p.pid=c.id )
select s.Id, s.Name, s.PId from cte c left join dbo.station s on s.id=c.Id order by PId
